        me.s0 = 0.8633289230056107 - mash(seed)
        if (me.s0 < 0) {
            me.s0 += 1
        }
        me.s1 = 0.15019597788341343 - mash(seed)
        if (me.s1 < 0) {
            me.s1 += 1
        }
        me.s2 = 0.9176952994894236 - mash(seed)
        if (me.s2 < 0) {
            me.s2 += 1
        }
        mash = null
